import { MessageService } from 'primeng/api';
import * as i0 from "@angular/core";
import * as i1 from "../equipe.service";
import * as i2 from "primeng/api";
import * as i3 from "@angular/router";
export class EquipListComponent {
    constructor(equipService, messageService, router) {
        this.equipService = equipService;
        this.messageService = messageService;
        this.router = router;
        this.colaboladores = [];
    }
    ngOnInit() {
        this.listarTodos(0);
    }
    async listarTodos(status) {
        await this.equipService.listarTodos(status)
            .then(rs => {
            this.colaboladores = rs;
        })
            .catch(error => this.messageService.add({ severity: 'error', summary: 'Erro', detail: 'Não foi possível carregar os dados!' }));
    }
    novoPost() {
        this.router.navigate(["equipe-cad"]);
    }
    editarColaborador(colaborador) {
        this.router.navigate(["blog-cad/" + colaborador.id]);
        console.log(colaborador);
    }
    controlarAtivo(colaborador) {
        this.equipService.alterarStatus(colaborador.status)
            .then(rs => {
            this.colaboladores = rs;
            this.listarTodos(0);
        })
            .catch(error => this.messageService.add({ severity: 'error', summary: 'Erro', detail: 'Não foi possível alterar o status!' }));
    }
    voltar() {
        this.router.navigate([""]);
    }
}
